import React from 'react';
import * as THREE from 'three';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';

const shooter = () => {
    class Thre {
        constructor(mountRef) {
          // Set up the scene, camera, and renderer
          this.scene = new THREE.Scene();
          this.camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
          this.camera.position.set(0, 0, 4);

          // Set up lights
          const ambientLight = new THREE.HemisphereLight(0xffffff, 0xbbbbff, 0.5);
          const directionalLight = new THREE.DirectionalLight(0xffffff, 1);
          directionalLight.position.set(0.2, 1, 1);

          this.scene.add(ambientLight);
          this.scene.add(directionalLight);

          this.renderer = new THREE.WebGLRenderer({ antialias: true });
          this.renderer.setSize(window.innerWidth, window.innerHeight);
          this.renderer.setAnimationLoop(this.render.bind(this));
          this.scene.background = new THREE.Color(0xaaaaaa);

          // Create star geometry
          const geometry = this.createStarGeometry();
          const material = new THREE.MeshStandardMaterial({ color: 0xff0000 });
          this.mesh = new THREE.Mesh(geometry, material);
          this.scene.add(this.mesh);

          this.controls = new OrbitControls(this.camera, this.renderer.domElement);

          // Append the renderer to the DOM
          if (mountRef.current) {
            mountRef.current.appendChild(this.renderer.domElement);
          } else {
            console.error('Mount reference not found');
            return;
          }


        }

        // Create star geometry function
        createStarGeometry(innerRadius = 0.4, outerRadius = 0.8, points = 5) {
          const shape = new THREE.Shape();
          const PI2 = Math.PI * 2;
          const inc = PI2 / (points * 2);

          let inner = true;
          shape.moveTo(outerRadius, 0); // Start at the outer radius point

          for (let theta = inc; theta < PI2; theta += inc) {
            const radius = inner ? innerRadius : outerRadius;
            shape.lineTo(Math.cos(theta) * radius, Math.sin(theta) * radius);
            inner = !inner;
          }

          shape.lineTo(outerRadius, 0); // Close the shape

          return new THREE.ShapeGeometry(shape);
        }

        // Render function
        render() {
          this.mesh.rotateY(0.01);
          this.renderer.render(this.scene, this.camera);
        }
      }
  return (
    <div>

    </div>
  )
}

export default shooter
